import React, { useEffect, useRef, useContext, useState } from "react";
import { Link } from "react-router-dom";
import "../styles/Menu.css";
import MenuContext from "../context/MenuContext.js";
import { jwtDecode } from "jwt-decode";

export default function Menu({ hamburgerRef }) {
  const { isMenuOpen, toggleMenu } = useContext(MenuContext);
  const menuRef = useRef(null);
  const [role, setRole] = useState(0);
  const [sessionClicked, setSessionClicked] = useState(false);

  const closeMenu = () => {
    toggleMenu();
  };

  useEffect(() => {
    const token = localStorage.getItem("token");
    const decodedToken = jwtDecode(token);
    console.log(decodedToken);
    setRole(decodedToken.role);
    console.log(role);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    closeMenu();
  };

  const handleSessionClick = () => {
    setSessionClicked(true);
  };

  useEffect(() => {
    const handleClickOutsideMenu = (event) => {
      if (
        menuRef.current &&
        !menuRef.current.contains(event.target) &&
        isMenuOpen &&
        event.target !== hamburgerRef.current &&
        event.target.parentNode !== hamburgerRef.current &&
        !sessionClicked // Kontrollera om en session har klickats
      ) {
        closeMenu();
      }
    };

    document.addEventListener("mousedown", handleClickOutsideMenu);

    return () => {
      document.removeEventListener("mousedown", handleClickOutsideMenu);
    };
  }, [isMenuOpen, sessionClicked]);

  return (
    <div className={`menu-wrapper ${isMenuOpen ? "open" : ""}`} ref={menuRef}>
      {role >= 2000 && (
        <Link to="/my-athletes" className="menu-item" onClick={closeMenu}>
          <h2 className="menu-item-h2">Mina Atleter</h2>
        </Link>
      )}

      {role >= 2000 && (
        <Link to="/exercises" className="menu-item" onClick={closeMenu}>
          <h2 className="menu-item-h2">Hantera övningar</h2>
        </Link>
      )}

      <Link
        to="/my-sessions"
        className="menu-item"
        onClick={() => {
          handleSessionClick();
          closeMenu();
        }}
      >
        <h2 className="menu-item-h2">Mina pass</h2>
      </Link>

      <div className="menu-item">
        <h2 className="menu-item-h2">Mina tävlingar</h2>
      </div>

      <Link to="/timer" className="menu-item" onClick={closeMenu}>
        <div>
          <h2 className="menu-item-h2">Timer</h2>
        </div>
      </Link>

      <Link to="/settings" className="menu-item" onClick={closeMenu}>
        <div>
          <h2 className="menu-item-h2">Inställningar</h2>
        </div>
      </Link>

      <Link to="/login" className="menu-item" id="logout" onClick={handleLogout}>
        <div>
          <h4>Logga ut</h4>
        </div>
      </Link>
    </div>
  );
}
