import React, {useState} from 'react'
import "./styles/addExcercise.css"

export default function AddExcercise( {isOpen} ) {
const [inputValue, setInputValue] = useState("")
const [excerciseDesc, setExcercisDesc] = useState("")
const [excercises, setExcercises] = useState([])


const handleChange = (event) => {
    const { name, value } = event.target;
    if (name === 'name') {
      setInputValue(value);
    } else if (name === 'description') {
        setExcercisDesc(value);
    }
  };

  const handleSaveExercise = () => {
    if (inputValue.trim() !== '' && excerciseDesc.trim() !== '') {
      const newExercise = { name: inputValue, description: excerciseDesc };
      setExcercises([...exerciseArray, newExercise]);
      setInputValue("");
      setExcercisDesc("");
    }
  };

  return (
    <div className="home-wrapper" style={{ filter: isOpen ? "blur(4px) brightness(40%)" : "blur(0) brightness(100%)" }}>
        <h1 className="view-header">Lägg till övning</h1>

        <div className="input-wrapper">
            <div className="input-name">
            <label for="name">Namn</label>
            <input type="text" id="name" name="name" placeholder="Raka marklyft" value={inputValue} onChange={handleChange}></input>
            </div>

        <div className="textarea-description">
            <label for="description">Beskrivning</label>
            <textarea name="description" rows="10" placeholder="Raka ben med långsamma och kontrollerade rörelser"></textarea>
        </div>
         </div>

        <div className="save-button-wrapper">
            <button className="save-button">Spara övning</button>
        </div>
    </div>
  )
}
