import React, { useEffect, useRef } from 'react';
import ReactDOM from 'react-dom';
import "../styles/Modal.css";

const Modal = ({ isOpen, onClose, children }) => {
    const modalRoot = useRef(document.createElement('div')); // Skapar en referens till modalens rot

    useEffect(() => {
      const existingModalRoot = document.getElementById('modal-root');
      existingModalRoot.appendChild(modalRoot.current);

      // Eventlyssnare för att stänga modalen när man klickar utanför den
      const handleClickOutside = (event) => {
        console.log('Click event', event); // Lägg till denna logg
        if (!modalRoot.current.contains(event.target)) {
          onClose();
        }
      };

      if (isOpen) {
        document.addEventListener('mousedown', handleClickOutside);
      } else {
        document.removeEventListener('mousedown', handleClickOutside);
      }

      return () => {
        existingModalRoot.removeChild(modalRoot.current);
        document.removeEventListener('mousedown', handleClickOutside);
      };
    }, [isOpen, onClose]);

    console.log('Modal rendering'); // Lägg till denna logg

    if (!isOpen) return null;

    return ReactDOM.createPortal(
      <div className="modal-overlay">
        <div className="modal">
          {children}
        </div>
      </div>,
      modalRoot.current
    );
  };

  export default Modal
