import React, {useState, useEffect, useContext, useRef} from 'react'
import "../styles/mySessions.css";
import MenuContext from "../context/MenuContext.js";
import Header from '../components/Header';
import Menu from '../components/Menu';
import {jwtDecode} from "jwt-decode"


export default function MySessions() {
    const modalRef = useRef();
    const hamburgerRef = useRef(null);
    const [sessions, setSessions] = useState([])
    const [role, setRole] = useState(0)
    const [user, setUser] = useState({})
    const [email, setEmail]= useState("")
    const { toggleMenu, isMenuOpen, setIsMenuOpen } = useContext(MenuContext);
 console.log("sessions", sessions)
 console.log("userSessions", userSessions)

    useEffect(() => {
        const getSessions = async () => {
            const token = localStorage.getItem("token")
            const decodedToken = jwtDecode(token)
            setRole(decodedToken.role)
            console.log(decodedToken.role
                )
            if (token) {
                try {
                        const response = await fetch(
                        "http://localhost:5000/get-sessions",
                        {
                          method: "GET",
                          headers: {
                            Authorization: `Bearer ${token}`,
                          },
                        },

                      )
                      const sessions = await response.json()
                      console.log(sessions)

                      const currentDate = new Date().toISOString().slice(0, 10)
                      if (role >= 2000) {
                         setSessions(sessions)
                          console.log("Fetching all sessions");
                      } else {
                          const userSessions = sessions.filter(session => session.attendees.includes(decodedToken.email) && session.date >= currentDate);
                          setSessions(userSessions)
                          console.log("User sessions:", userSessions);
                      }

                      console.log(sessions)
                } catch (err) {
                    console.error("Couldn't get sessions", err)
                }
            }
        }
        getSessions()
    },[role])


  return (
    <div>
    <Header onMenuToggle={toggleMenu} hamburgerRef={hamburgerRef} />
    <Menu
      isOpen={isMenuOpen}
      setIsOpen={setIsMenuOpen}
      hamburgerRef={hamburgerRef}
    />

    <div
    className="home-wrapper"
    style={{
      filter: isMenuOpen
        ? "blur(4px) brightness(40%)"
        : "blur(0) brightness(100%)",
    }}
  >
        <h1 className="view-header">Mina pass</h1>



  <div className="sessions-wrapper">
    <div className="sessions-header">
      <h2>Dagens Pass</h2>
    </div>


        <div className="sessions" >
          <div className="sessions-content">
            <div className="session-top">
              <h2>
                Hej
              </h2>
            </div>
            <div className="session-bottom">
              <h2> Hej</h2>
              <div className="session-initials">

                  <span className="initials-wrapper">
                    <h3> Hej</h3>
                  </span>

              </div>
            </div>
          </div>
        </div>

  </div>

    </div>
</div>
  )
}
