import React, { useEffect, useRef } from 'react';
import "./styles/Menu.css";

export default function Menu({ isOpen }) {
  const menuRef = useRef(null);

  useEffect(() => {
    const handleClickOutsideMenu = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target) && isOpen) {
        console.log("Stänger menyn");
      }
    };

    document.addEventListener('mousedown', handleClickOutsideMenu);

    return () => {
      document.removeEventListener('mousedown', handleClickOutsideMenu);
    };
  }, [isOpen]);

  return (
    <div className={`menu-wrapper ${isOpen ? "open" : ""}`} style={{ transform: isOpen ? 'translateX(0%)' : 'translateX(100%)' }} ref={menuRef}>
        <div className="menu-item">
            <h2>Skapa pass</h2>
        </div>

        <div className="menu-item">
            <h2>Lägg till övning</h2>
        </div>

        <div className="menu-item">
            <h2>Lägg till atlet</h2>
        </div>

        <div className="menu-item">
            <h2>Mina pass</h2>
        </div>

        <div className="menu-item">
            <h2>Tävlingar</h2>
        </div>

        <div className="menu-item">
            <h2>Timer</h2>
        </div>

        <div className="menu-item" id="logout">
            <h4>Logga ut</h4>
        </div>
    </div>
  )
}
