import React, { useEffect, useState } from "react";

const Weather = ({ sessionDate, sessionTime }) => {
  const [userLocation, setUserLocation] = useState(null);
  const [weatherData, setWeatherData] = useState(null);
  const API_KEY = '19c737dffbd944c5114401ce4fb6d57a';

  useEffect(() => {
    const fetchWeatherData = async () => {
      try {
        if (!userLocation) return;

        const { latitude, longitude } = userLocation;

        const sessionDateTime = new Date(`${sessionDate}T${sessionTime}`);
        const sessionTimestamp = Math.round(sessionDateTime.getTime() / 1000);

        const response = await fetch(
          `https://api.openweathermap.org/data/2.5/forecast?lat=${latitude}&lon=${longitude}&units=metric&dt=${sessionTimestamp}&appid=${API_KEY}`
        );

        if (response.ok) {
          const data = await response.json();
          setWeatherData(data);
        } else {
          console.error("Failed to fetch weather data");
        }
      } catch (error) {
        console.error("Error fetching weather data:", error);
      }
    };

    if (userLocation) {
      fetchWeatherData();
    }
  }, [userLocation, sessionDate, sessionTime]);

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        setUserLocation({
          latitude: position.coords.latitude,
          longitude: position.coords.longitude,
        });
      });
    }
  }, []);

  const getWeatherEmoji = (weather) => {
    switch (weather) {
      case "Clear":
        return "‚òÄÔ∏è";
      case "Clouds":
        return "‚òÅÔ∏è";
      case "Rain":
        return "üåßÔ∏è";
      case "Snow":
        return "‚ùÑÔ∏è";
      default:
        return "";
    }
  };

  if (!weatherData) {
    return <p>Loading weather...</p>;
  }

  return (
    <div>
      {weatherData.list.map((weatherItem) => (
        <div key={weatherItem.dt}>
          <p>{getWeatherEmoji(weatherItem.weather[0].main)} </p>
          <p>Temperature: {weatherItem.main.temp} ¬∞C</p>
          <p>Description: {weatherItem.weather[0].description}</p>
          {/* Add more weather information as needed */}
        </div>
      ))}
    </div>
  );
};

export default Weather;
