import React, { useState, useEffect, useRef } from 'react';
import "./styles/Menu.css"

export default function Menu({ isOpen, onClose }) {
    const menuRef = useRef(null);

    useEffect(() => {
      function handleClickOutside(event) {
        if (menuRef.current && !menuRef.current.contains(event.target)) {
          onClose(); // Stäng menyn när användaren klickar utanför
        }
      }

      // Lägg till klickhändelselyssnare när komponenten monteras
      document.addEventListener('mousedown', handleClickOutside);

      // Ta bort klickhändelselyssnare när komponenten avmonteras
      return () => {
        document.removeEventListener('mousedown', handleClickOutside);
      };
    }, [onClose]); // onClose ska inkluderas som beroende för att säkerställa att rätt funktion används

    return (
      <div
        className="menu-wrapper"
        style={{ transform: isOpen ? 'translateX(0)' : 'translateX(100%)' }}
        ref={menuRef}
      >
        <div className="menu-item">
            <h2>Skapa pass</h2>
        </div>

        <div className="menu-item">
            <h2>Lägg till övning</h2>
        </div>

        <div className="menu-item">
            <h2>Lägg till pass</h2>
        </div>

        <div className="menu-item">
            <h2>Timer</h2>
        </div>

        <div className="menu-item" id="logout">
            <h4>Logga ut</h4>
        </div>

    </div>
  )
}
